my @array = (1, 2, 3);my $size = @array; # Get the array sizeif ($size > 0) {my $index = 0; #example of safe access, always check the index is less than the size of the arrayif ($index < $size){my $element = $array[$index];print "Element at index $index: $element\n";}else{print "Index out of bounds\n";}}else{print "Array is empty\n";}